mapper.select:
select questionnaire_id, name, customer_type, business_type
from questionnaires
where business_type = #{dto.businessType}

select question_id, questionnaire_id, content
from questions
where questionnaire_id = #{dto.questionnaireId}

select option_id, content
from options
where question_id = #{dto.questionId}

select score
from options
where question_id = #{dto.questionId} and option_id = #{dto.optionId}
